import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/StockChart.css";

const StockChart = () => {
  const [topStocks, setTopStocks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchedStock, setSearchedStock] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopStocks = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5001/api/topstocks");
        setTopStocks(response.data);
      } catch (err) {
        setError("Failed to load stock data.");
      } finally {
        setLoading(false);
      }
    };

    fetchTopStocks();
  }, []);

  const handleSearch = async () => {
    if (!searchQuery) return;
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5001/api/stock/${searchQuery}`);
      setSearchedStock(response.data);
      setError(null);
    } catch (err) {
      setSearchedStock(null);
      setError("Stock not found. Try a valid symbol.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="stock-container">
      <video className="background-video" autoPlay loop muted>
        <source src="/assets/videos/stockmarket.mp4" type="video/mp4" />
      </video>

      <div className="content">
        <h2 className="title">Stock Market Overview</h2>

        <div className="search-bar">
          <input
            type="text"
            placeholder="Search stock by symbol (e.g., AAPL, TSLA)"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value.toUpperCase())}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {loading && <p className="loading">Loading...</p>}
        {error && <p className="error">{error}</p>}

        {searchedStock && (
          <div className="stock-info">
            <h3>{searchedStock.name} ({searchedStock.symbol})</h3>
            <p>Price: ${searchedStock.price}</p>
            <p>Market Cap: {searchedStock.marketCap}</p>
            <p>Day High: {searchedStock.dayHigh}</p>
            <p>Day Low: {searchedStock.dayLow}</p>
          </div>
        )}

        <div className="top-stocks">
          <h3>Market Leaders</h3>
          <ul>
            {topStocks.map((stock) => (
              <li key={stock.symbol}>{stock.name} ({stock.symbol}): ${stock.price}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default StockChart;
