import { useState } from "react";
import axios from "axios";

const ChatBot = () => {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const userId = "user123"; // Example user ID

  const sendMessage = async () => {
    if (!message) return;

    const newChat = [...chat, { user: "You", text: message }];
    setChat(newChat);
    setMessage("");

    try {
      const response = await axios.post("http://localhost:5001/api/chat", { userId, message });
      setChat([...newChat, { user: "Bot", text: response.data.response }]);
    } catch (err) {
      console.error("Chatbot error:", err);
    }
  };

  return (
    <div>
      <h1>Agentic AI Chatbot</h1>
      <div>{chat.map((msg, index) => <p key={index}><strong>{msg.user}:</strong> {msg.text}</p>)}</div>
      <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Ask something..." />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatBot;
