import React from "react";
import "../styles/HomePage.css";
import stockVideo from "../assets/stockmarket.mp4";

const HomePage = () => {
  return (
    <div className="homepage">
      {/* Background Video */}
      <video autoPlay loop muted className="background-video">
        <source src={stockVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content Section */}
      <div className="content">
        <h1>Welcome to StockWiz Academy</h1>
        <p>
          Gain in-depth knowledge of the stock market with AI-powered insights, real-time data, and interactive learning tools.  
        </p>

        <div className="features">
          <h2>Key Features</h2>
          <ul>
            <li><strong>Live Stock Market Data</strong> – Stay updated with real-time stock prices, market trends, and company analysis.</li>
            <li><strong>AI-Powered Chatbot</strong> – Get instant answers to finance-related questions using advanced AI and document-based search.</li>
            <li><strong>Stock Analysis Tools</strong> – Access detailed stock charts and performance metrics to make informed decisions.</li>
            <li><strong>Educational Videos</strong> – Learn stock market concepts through curated YouTube tutorials.</li>
            <li><strong>Smart Stock Search</strong> – Quickly find detailed insights on any stock by searching its symbol.</li>
          </ul>
        </div>

        <p className="cta">Start exploring and take your stock market knowledge to the next level.</p>
      </div>
    </div>
  );
};

export default HomePage;
