import { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import Chart from "chart.js/auto";
import stockVideo from "../assets/stockmarket.mp4";

const StockChart = () => {
  const [symbol, setSymbol] = useState("");
  const [stockInfo, setStockInfo] = useState(null);
  const [topStocks, setTopStocks] = useState([]);
  const [chartData, setChartData] = useState(null);

  // Fetch top 12 stocks on page load
  useEffect(() => {
    fetchTopStocks();
  }, []);

  const fetchTopStocks = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/topstocks");
      const data = await response.json();
      setTopStocks(data);
    } catch (error) {
      console.error("Error fetching top stocks:", error);
    }
  };

  const fetchStockData = async () => {
    if (!symbol) return;

    try {
      const response = await fetch(`http://localhost:5001/api/stock/${symbol}`);
      const data = await response.json();
      setStockInfo(data);

      // Prepare data for chart
      setChartData({
        labels: data.history.map((entry) => entry.date),
        datasets: [
          {
            label: `${data.name} Price`,
            data: data.history.map((entry) => entry.price),
            borderColor: "#00BFFF",
            backgroundColor: "rgba(0, 191, 255, 0.3)",
          },
        ],
      });
    } catch (error) {
      console.error("Error fetching stock data:", error);
    }
  };

  return (
    <div className="stock-container">
      {/* Background Video */}
      <video autoPlay muted loop className="background-video">
  <source src={stockVideo} type="video/mp4" />
</video>


      <div className="content">
        <h2>📈 Real-Time Stock Market</h2>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Enter stock symbol (e.g., AAPL)"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          />
          <button onClick={fetchStockData}>🔍 Search</button>
        </div>

        {/* Top 12 Stocks */}
        <div className="top-stocks">
          <h3>🔥 Top 12 Stocks</h3>
          <ul>
            {topStocks.map((stock) => (
              <li key={stock.symbol}>
                {stock.name} ({stock.symbol}) - ${stock.price}
              </li>
            ))}
          </ul>
        </div>

        {/* Stock Info */}
        {stockInfo && (
          <div className="stock-info">
            <h3>{stockInfo.name} ({symbol})</h3>
            <p>📊 Price: ${stockInfo.price}</p>
            <p>⬆️ High: ${stockInfo.high} | ⬇️ Low: ${stockInfo.low}</p>
            <p>📌 Open: ${stockInfo.open} | 💰 Market Cap: ${stockInfo.marketCap}</p>
            <p>📉 Previous Close: ${stockInfo.previousClose}</p>
            <p>🔄 Volume: {stockInfo.volume}</p>

            {/* Stock Price Chart */}
            {chartData && (
              <div className="chart-container">
                <Line data={chartData} />
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default StockChart;
