import React, { useEffect, useState } from "react";
import axios from "axios";

const StockChart = () => {
  const [topStocks, setTopStocks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchedStock, setSearchedStock] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopStocks = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5001/api/topstocks");
        if (Array.isArray(response.data)) {
          setTopStocks(response.data);
        } else {
          throw new Error("Invalid data format");
        }
      } catch (err) {
        console.error("❌ Error fetching top stocks:", err.message);
        setError("Failed to load stock data.");
      } finally {
        setLoading(false);
      }
    };

    fetchTopStocks();
  }, []);

  const handleSearch = async () => {
    if (!searchQuery) return;
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5001/api/stock/${searchQuery}`);
      setSearchedStock(response.data);
      setError(null);
    } catch (err) {
      console.error("❌ Error fetching stock:", err.message);
      setSearchedStock(null);
      setError("Stock not found. Try a valid symbol.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="stock-container">
      <video className="background-video" autoPlay loop muted>
      <source src="/assets/videos/stockmarket.mp4" type="video/mp4" />
      </video>

      <div className="content">
        <h2>Top 12 Stocks</h2>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search stock by symbol (e.g., AAPL, TSLA)"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value.toUpperCase())}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {loading && <p>Loading...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {/* Display searched stock */}
        {searchedStock && (
          <div className="stock-info">
            <h3>{searchedStock.name} ({searchedStock.symbol})</h3>
            <p>Price: ${searchedStock.price.toFixed(2)}</p>
            <p>Change: {searchedStock.change.toFixed(2)}%</p>
          </div>
        )}

        {/* Display Top Stocks */}
        <div className="top-stocks">
          <h3>Market Leaders</h3>
          <ul>
            {topStocks.map((stock) => (
              <li key={stock.symbol}>
                <strong>{stock.name}</strong> ({stock.symbol}) - ${stock.price.toFixed(2)} 
                <span style={{ color: stock.change >= 0 ? "#32CD32" : "#FF4136" }}>
                  ({stock.change.toFixed(2)}%)
                </span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default StockChart;
