import React, { useState, useEffect } from 'react';

const StockChart = () => {
    const [stocks, setStocks] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedStock, setSelectedStock] = useState(null);

    useEffect(() => {
        fetch('https://query1.finance.yahoo.com/v7/finance/quote?symbols=AAPL,MSFT,GOOGL,AMZN,TSLA,META,NFLX,NVDA,AMD,BABA,DIS,UBER')
            .then(response => response.json())
            .then(data => {
                if (data.quoteResponse && data.quoteResponse.result) {
                    setStocks(data.quoteResponse.result);
                }
            })
            .catch(error => console.error('Error fetching stock data:', error));
    }, []);

    const handleSearch = () => {
        const stock = stocks.find(s => s.symbol.toLowerCase() === searchQuery.toLowerCase());
        setSelectedStock(stock || null);
    };

    return (
        <div className="stock-container">
            <input
                type="text"
                placeholder="Search stock symbol..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>

            {selectedStock ? (
                <div className="stock-details">
                    <h2>{selectedStock.shortName} ({selectedStock.symbol})</h2>
                    <p>Current Price: ${selectedStock.regularMarketPrice}</p>
                    <p>Open: ${selectedStock.regularMarketOpen}</p>
                    <p>High: ${selectedStock.regularMarketDayHigh}</p>
                    <p>Low: ${selectedStock.regularMarketDayLow}</p>
                    <p>Close: ${selectedStock.regularMarketPreviousClose}</p>
                    <p>Volume: {selectedStock.regularMarketVolume}</p>
                </div>
            ) : (
                <div className="stock-list">
                    {stocks.map(stock => (
                        <div key={stock.symbol} className="stock-item">
                            <span className="stock-symbol">{stock.symbol}</span>
                            <span className="stock-price">${stock.regularMarketPrice}</span>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default StockChart;
