import React, { useState } from "react";
import "../styles/LearningVideos.css";

const videos = [
  {
    id: "ZVhBVFNXWxU",
    title: "Stock Market Basics",
    description: "Learn the fundamentals of stock markets.",
  },
  {
    id: "H_oXWjMyS2k",
    title: "Technical Analysis Explained",
    description: "How to analyze stocks using technical indicators.",
  },
  {
    id: "wNSXrzjG6hA",
    title: "How to Invest in Stocks",
    description: "A complete beginner’s guide to investing.",
  },
  {
    id: "bJfwXH13XQo",
    title: "Trading Strategies That Work",
    description: "Practical strategies for successful stock trading.",
  },
  {
    id: "sE44hEFu8Lw",
    title: "Understanding Candlestick Patterns",
    description: "Learn how to read and use candlestick charts.",
  },
  {
    id: "KTZ8j4NnV9o",
    title: "How to Manage Risk in Stock Trading",
    description: "Techniques to minimize risk in investments.",
  },
];

const LearningVideos = () => {
  const [currentVideo, setCurrentVideo] = useState(videos[0].id);

  const handleVideoError = (failedId) => {
    console.warn(`Video ${failedId} failed to load. Skipping to next.`);
    const nextVideo = videos.find((v) => v.id !== failedId);
    if (nextVideo) setCurrentVideo(nextVideo.id);
  };

  return (
    <div className="learning-videos-page">
      <h1>Stock Market Learning Videos</h1>
      <p>Watch tutorials to learn about the stock market.</p>

      {/* Video Player */}
      <div className="video-player">
        <iframe
          width="100%"
          height="400"
          src={`https://www.youtube.com/embed/${currentVideo}`}
          title="Stock Market Video"
          allowFullScreen
          onError={() => handleVideoError(currentVideo)}
        ></iframe>
      </div>

      {/* Video Grid */}
      <div className="video-grid">
        {videos.map((video) => (
          <div
            key={video.id}
            className="video-card"
            onClick={() => setCurrentVideo(video.id)}
          >
            <img
              src={`https://img.youtube.com/vi/${video.id}/mqdefault.jpg`}
              alt={video.title}
              className="video-thumbnail"
            />
            <h3>{video.title}</h3>
            <p>{video.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearningVideos;
